create database f3_monkey;
use f3_monkey;

create table khachhang(
	ma int primary key not null auto_increment,
	tendangnhap varchar(100),
    hoten varchar(100),
    diachi text,
    sodienthoai varchar(20),
    matkhau varchar(100)
);


create table nhacungcap(
	ma int primary key not null auto_increment,
    ten varchar(100),
    sodienthoai varchar(20),
    diachi text,
    email text,
    nguoilienhe text
);

create table banner(
	ma int primary key not null auto_increment,
    anh varchar(100),
    link varchar(100)
);

create table slider(
	ma int primary key not null auto_increment,
    anh varchar(100),
    link varchar(100),
	noidung varchar(100)
);

create table danhmuc(
	ma int primary key not null auto_increment,
    ten varchar(100),
    ghichu varchar(100),
    link varchar(100)
);

create table thuonghieu(
	ma int primary key not null auto_increment,
    ten varchar(100),
    ghichu varchar(100),
    link varchar(100),
    madanhmuc int,
	foreign key (madanhmuc) references danhmuc(ma)
);

create table sanpham(
	ma int primary key not null auto_increment,
    ten varchar(100),

    mathuonghieu int,
    motasanpham text default '',
    tinhtrang int default 1, -- 1 la` moi, 0 la cu
    soluongtrongkho int default 0,
    soluongban int default 0,
    danhgiatrungbinh int default 0,
	foreign key (mathuonghieu) references thuonghieu(ma),
    madanhmuc int,
	foreign key (madanhmuc) references danhmuc(ma)
);

create table anhsanpham(
    ma int primary key not null auto_increment,
    anh varchar(100),
    masanpham int,
	foreign key (masanpham) references sanpham(ma)
);

create table thongsosanpham(
    ma int primary key not null auto_increment,
    ten varchar(100),
    giatri varchar(100),
    masanpham int,
	foreign key (masanpham) references sanpham(ma)
);

create table khuyenmaisanpham(
    ma int primary key not null auto_increment,
    ngaybatdau datetime default now(),
    khuyenmai varchar(100),
    masanpham int,
	foreign key (masanpham) references sanpham(ma)
);

create table banggia(
	ma int primary key not null auto_increment,
	gia int,
    ngaybatdau datetime default now(),
    giamgia int,
    masanpham int,
	foreign key (masanpham) references sanpham(ma)
);

create table phieunhap (
    ma int primary key not null auto_increment,
    ngaynhap datetime default now(),
    manhacungcap int,
    tongtien int, 
    lydonhap varchar(200),
    foreign key (manhacungcap) references nhacungcap(ma)
);

create table chitietphieunhap (
	ma int primary key not null auto_increment,
    maphieunhap  int,
    masanpham int,
    soluong int,
    dongia int,
    foreign key (maphieunhap) references phieunhap(ma),
    foreign key (masanpham) references sanpham(ma)
);

create table thanhtoan(
	ma int primary key not null auto_increment,
    hinhthucthanhtoan varchar(100),
	trangthai int,
    ngaythanhtoan datetime
);

create table vanchuyen(
	ma int primary key not null auto_increment,
    tennguoinhan varchar(100),
	diachi int,
    sodienthoai datetime,
    ghichu varchar(100)
);

create table donhang(
	ma int primary key not null auto_increment,
    makhachhang int,
	mavanchuyen int,
	mathanhtoan int,
    trangthai int,
    thanhtien int,
    ngaytao datetime,
	foreign key (makhachhang) references khachhang(ma),
	foreign key (mathanhtoan) references thanhtoan(ma),
	foreign key (mavanchuyen) references vanchuyen(ma)
);


create table chitietdonhang(
	ma int primary key not null auto_increment,
    madonhang int,
	masanpham int,
	soluong int,
	foreign key (masanpham) references sanpham(ma),	
    foreign key (madonhang) references donhang(ma)
);

create table loaitaikhoan(
	ma int primary key not null auto_increment,
	tenloai varchar(100)
);


create table user(
	ma int primary key not null auto_increment,
	ten varchar(100),
    tenhienthi varchar(100),
	matkhau varchar(100),
    anh varchar(100) default '/backend/images/user.png',
    maloaitaikhoan int default 1,
    foreign key (maloaitaikhoan) references loaitaikhoan(ma)
);

